import React, { useEffect } from "react";
import "./HomePage.css";
import { Card, Button, CardBody, CardTitle, CardSubtitle, CardText } from "reactstrap";
import mermaid from "mermaid";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";
import { FaPizzaSlice, FaHamburger } from "react-icons/fa";
import { IoFastFoodOutline } from "react-icons/io5";
import { GiFrenchFries } from "react-icons/gi";
import { CiPizza } from "react-icons/ci";
import { MdOutlineFastfood } from "react-icons/md";


const HomePage = () => {
  useEffect(() => {
    // Sayfa yüklendiğinde Mermaid graflarını çizelim
    mermaid.render("mermaidGraph1", mermaidGraph1, (svgCode) => {
      document.getElementById("graph1").innerHTML = svgCode;
    });

    mermaid.render("mermaidGraph2", mermaidGraph2, (svgCode) => {
      document.getElementById("graph2").innerHTML = svgCode;
    });
  }, []);

  return (
    <div className="Top">
      <div className="Main">
        <h1>
          Teknolojik Yemekler <br></br>Kod Acıktırır<br></br>Pizza, Doyurur
        </h1>
        <br></br>
        <div />
        <div />
        <div className="topbutton">
          <Link to="/pizza">
            <button id="order-pizza">Pizza Siparişi Ver</button>
          </Link>
        </div>
      </div>
      <div className="socialtop">
        <FaPizzaSlice />
        <IoFastFoodOutline />
        <GiFrenchFries />
        <FaHamburger />
        <CiPizza />
        <MdOutlineFastfood />
      </div>
      <div className="mid">
        <Card style={{ width: "25rem" }}>
          <img alt="Sample" src={`${process.env.PUBLIC_URL}/esnek/kart-1.png`} />

          <CardBody>
            <CardTitle tag="h5">Özel Lezzetus</CardTitle>
            <CardSubtitle className="mb-2 text-muted" tag="h6">
              Position : Absolute Acılı Pizzza
            </CardSubtitle>
            <CardText>Kendine Güvenenlerin Seçimi</CardText>
            <Link to="/pizza">
              <Button> Sipariş Ver</Button>
            </Link>
          </CardBody>
        </Card>
        <div className="twoside">
          {/* Diğer kartlar... */}

          {/* Mermaid grafları için div'leri ekleyelim */}
          <div id="graph1" className="mermaid-graph"></div>
          <div id="graph2" className="mermaid-graph"></div>
        </div>
      </div>
      {/* Sayfanın geri kalanı... */}
    </div>
  );
};

export default HomePage;
