import React from "react";
import "./Order.css";
import * as Yup from "yup";
import { useState, useEffect } from "react";
import {
  Card,
  Button,
  CardBody,
  CardTitle,
  CardSubtitle,
  CardText,
} from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";

const formSchema = Yup.object().shape({
  isimDegeri: Yup.string()
    .required("İsim alanı zorunludur")
    .min(2, "İsim en az karakter olmalı"),
  Malzeme: Yup.array()
    .min(4, "En az 4 malzeme seçebilirsiniz")
    .max(10, "En fazla 10 malzeme seçebilirsiniz"),
});

const Check = () => {
  const [formData, setFormData] = useState({ Malzeme: [] });
  const [buttonDisabledMi, setButtonDisabledMi] = useState(true);
  const [errors, setErrors] = useState({ Malzeme: "" });
  useEffect(() => {
    formSchema.isValid(formData).then((valid) => setButtonDisabledMi(!valid));
  }, [formData]);

  const checkFormErrors = (name, value) => {
    Yup.reach(formSchema, name)
      .validate(value)
      .then(() => {
        setErrors((errors) => ({
          ...errors,
          [name]: "",
        }));
      })
      .catch((err) => {
        setErrors((errors) => ({
          ...errors,
          [name]: err.errors[0],
        }));
      });
  };

  function handleChange(event) {
    const { value, type, name } = event.target;

    if (type === "checkbox") {
      let yeniMalzemeler;
      if (formData.Malzeme.includes(value)) {
        yeniMalzemeler = formData.Malzeme.filter((m) => m !== value);
      } else {
        yeniMalzemeler = [...formData.Malzeme, value];
      }

      checkFormErrors(name, yeniMalzemeler);
      setFormData({
        ...formData,
        [name]: yeniMalzemeler,
      });
      console.log("Seçilen Malzemeler: ", {
        ...formData,
        [name]: yeniMalzemeler,
      });
    } else {
      checkFormErrors(name, value);
      setFormData({
        ...formData,
        [name]: value,
      });
      console.log("Seçilen Malzemeler: ", { ...formData, [name]: value });
    }
  }

  return (
    <form>
      <h2>Ek Malzemeler</h2>
      <p>En Fazla 10 malzeme seçebilirsiniz. 5 TL </p>
      <div className="allfive">
        <div className="firstfive">
          <input
            onChange={handleChange}
            type="checkbox"
            id="food1"
            name="Malzeme"
            value="Pepperoni"
            checked={formData.Malzeme.includes("Pepperoni")}
          ></input>
          <label htmlFor="food1">Pepperoni</label>
          <br></br>

          <input
            onChange={handleChange}
            type="checkbox"
            id="food2"
            name="Malzeme"
            value="Sosis"
            checked={formData.Malzeme.includes("Sosis")}
          ></input>
          <label htmlFor="food2"> Sosis</label>
          <br></br>
          <input
            onChange={handleChange}
            type="checkbox"
            id="food3"
            name="Malzeme"
            value="Kanada jambonu"
            checked={formData.Malzeme.includes("Kanada jambonu")}
          ></input>
          <label htmlFor="food3"> Kanada jambonu</label>
          <br></br>
          <input
