import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import "./Radio.css";

const RadioDrop = () => {
  const [selectedSize, setSelectedSize] = useState("");
  const [selectedDough, setSelectedDough] = useState("");

  const handleSizeChange = (event) => {
    setSelectedSize(event.target.value);
  }

  const handleDoughChange = (event) => {
    setSelectedDough(event.target.value);
  }

  const logSelection = (selection, label) => {
    if (selection !== "") {
      console.log(`Seçilen ${label}:`, selection);
    }
  }

  return (
    <div className="radio">
      <form>
        <p>Boyut Seç *</p>

        {["Küçük", "Orta", "Büyük"].map((size) => (
          <div key={size}>
            <input
              type="radio"
              id={size.toLowerCase()}
              name="size"
              value={size}
              checked={selectedSize === size}
              onChange={handleSizeChange}
            />
            <label htmlFor={size.toLowerCase()}>{size}</label><br />
          </div>
        ))}
      </form>

      <form>
        <label htmlFor="dough">Hamur Seçiniz *</label><br />
        <select id="dough" value={selectedDough} onChange={handleDoughChange}>
          <option value="">Seçiniz</option>
          {["Kalın", "İnce", "Çok İnce"].map((dough) => (
            <option key={dough.toLowerCase()} value={dough}>{dough} Hamur</option>
          ))}
        </select>
      </form>

      {logSelection(selectedSize, "boyut")}
      {logSelection(selectedDough, "hamur")}
    </div>
  );
};

export default RadioDrop;
