import React, { useEffect } from "react";
import {
  BrowserRouter,
  Route,
  BrowserRouter as Router,
} from "react-router-dom";
import Order from "./Order.js";
import HomePage from "./HomePage.js";
import Final from "./Final.js";
import "./HomePage.css";
import mermaid from "mermaid/mermaid.esm.min.mjs";

// Mermaid konfigürasyonunu burada yapalım
mermaid.initialize({
  startOnLoad: true,
  theme: "default",
});

const App = () => {
  // useEffect içinde de konfigürasyon yapabilirsiniz
  useEffect(() => {
    // Örnek olarak bir useEffect kullanarak daha fazla iş yapabilirsiniz
    // Örneğin: başka bir kütüphanın yüklenmesi veya başlangıçta yapılması gereken diğer işlemler
  }, []);

  return (
    <BrowserRouter>
      <Router>
        <div className="Home">
          <Route exact path="/" component={HomePage} />
        </div>
        <div className="order">
          <Route exact path="/pizza" component={Order} />
        </div>
        <div className="last">
          <Route exact path="/final" component={Final} />
        </div>
      </Router>
    </BrowserRouter>
  );
};

export default App;
